import { ReactNode } from 'react';

export interface SpeechSynthesisHook {
  speak: (options: { text: string; voice?: SpeechSynthesisVoice; rate?: number; pitch?: number; volume?: number }) => void;
  cancel: () => void;
  speaking: boolean;
  supported: boolean;
  voices: SpeechSynthesisVoice[];
}

export interface SpeechRecognitionHook {
  listen: (options?: { continuous?: boolean; interimResults?: boolean }) => void;
  listening: boolean;
  stop: () => void;
  supported: boolean;
}

export interface SpeechRecognitionOptions {
  onResult?: (result: string) => void;
  onEnd?: () => void;
  onError?: (error: any) => void;
  continuous?: boolean;
  interimResults?: boolean;
  lang?: string;
}

export declare function useSpeechSynthesis(): SpeechSynthesisHook;
export declare function useSpeechRecognition(options?: SpeechRecognitionOptions): SpeechRecognitionHook;